import React from "react";
import "./AddTweetForm.css";
import localforage from "localforage";

class AddTweetForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tweet: "",
    };
  }

  handleTweetChange(event) {
    this.setState({ tweet: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    let date = new Date().toString();
    const newTweet = {
      tweet: this.state.tweet,
      dateCreated: Date.now(),
      dateReadable: date,
      author: yonathan,
    };
    localforage
      .setItem(`${newTweet.dateCreated}`, `${newTweet}`)
      .then(console.log(" tweet saved"));
    this.props.onAddTweet(newTweet);
    this.setState({ tweet: "" });
  }

  render() {
    return (
      <form className="form" onSubmit={(event) => this.handleSubmit(event)}>
        <div>
          <textarea
            type="text"
            name="tweet"
            id="tweet"
            required
            maxlength="140"
            placeholder="What you have in mind..."
            value={this.state.tweet}
            onChange={(event) => this.handleTweetChange(event)}
          />
        </div>
        <div>
          <button type="submit">Tweet</button>
        </div>
      </form>
    );
  }
}
export default AddTweetForm;
