import React from "react";
import AddTweetForm from "./components/AddTweetForm";
import TweetsList from "./components/TweetsList";
import localforage from "localforage";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: [],
    };
  }

  handleOnAddTweet(newTweet) {
    localforage
      .setItem(`${newTweet.dateCreated}`, `${newTweet}`)
      .then(console.log(" tweet saved"));
    this.setState((state) => {
      return { tweets: [newTweet, ...state.tweets] };
    });
  }
  componentDidMount() {
    let list = [];
    localforage
      .iterate(function (value, key, iterationNumber) {
        list.push(value);
      })
      .then(function () {
        console.log("Iteration has completed");
      })
      .catch(function (err) {
        // This code runs if there were any errors
        console.log(err);
      });
    this.setState({ tweets: list });
  }

  render() {
    localforage
      .clear()
      .then(function () {
        // Run this code once the database has been entirely deleted.
        console.log("Database is now empty.");
      })
      .catch(function (err) {
        // This code runs if there were any errors
        console.log(err);
      });
    return (
      <div className="main">
        <AddTweetForm
          onAddTweet={(newTweet) => this.handleOnAddTweet(newTweet)}
        />
        <TweetsList tweets={this.state.tweets} />
      </div>
    );
  }
}

export default App;
